// Parse Telegram quick log format
// Expected: /interact @username type:dm energy:8 green:ela riu muito yellow:chegou atrasada next:convite caf√©

const text = $input.item.json.message.text;
const chatId = $input.item.json.message.chat.id;

// Extract username
const usernameMatch = text.match(/@(\w+)/);
const username = usernameMatch ? usernameMatch[1] : 'unknown';

// Extract type
const typeMatch = text.match(/type:(\w+)/);
const interactionType = typeMatch ? typeMatch[1] : 'presencial';

// Extract energy
const energyMatch = text.match(/energy:(\d+)/);
const energy = energyMatch ? parseInt(energyMatch[1]) : 5;

// Extract flags
const greenMatch = text.match(/green:([^\s]+(?:\s+[^\s:]+)*?)(?=\s+(?:yellow|red|next):|$)/);
const yellowMatch = text.match(/yellow:([^\s]+(?:\s+[^\s:]+)*?)(?=\s+(?:green|red|next):|$)/);
const redMatch = text.match(/red:([^\s]+(?:\s+[^\s:]+)*?)(?=\s+(?:green|yellow|next):|$)/);
const nextMatch = text.match(/next:([^\s]+(?:\s+[^\s:]+)*?)$/);

const greenFlags = greenMatch ? [greenMatch[1].trim()] : [];
const yellowFlags = yellowMatch ? [yellowMatch[1].trim()] : [];
const redFlags = redMatch ? [redMatch[1].trim()] : [];
const nextMove = nextMatch ? nextMatch[1].trim() : '';

return {
  person: username,
  interaction_type: interactionType,
  interaction_date: new Date().toISOString(),
  her_energy: energy,
  topics_discussed: [],
  green_flags: greenFlags,
  yellow_flags: yellowFlags,
  red_flags: redFlags,
  next_move: nextMove,
  chat_id: chatId,
  source: 'telegram'
};

